package com.mcmmorpg.impl.npcs;

import org.bukkit.ChatColor;
import org.bukkit.Location;
import org.bukkit.Sound;

import com.mcmmorpg.common.character.AbstractCharacter;
import com.mcmmorpg.common.character.PlayerCharacter;
import com.mcmmorpg.common.sound.Noise;

public class Guard extends StaticHuman {

	private static final int LEVEL = 25;
	private static final String MALE_TEXTURE_DATA = "";
	private static final String MALE_TEXTURE_SIGNATURE = "";
	private static final String FEMALE_TEXTURE_DATA = "";
	private static final String FEMALE_TEXTURE_SIGNATURE = "";
	private static final Noise MALE_SPEAK_NOISE = new Noise(Sound.ENTITY_VILLAGER_AMBIENT, 1, 0.75f);
	private static final Noise FEMALE_SPEAK_NOISE = new Noise(Sound.ENTITY_VILLAGER_AMBIENT, 1, 1.25f);

	private final boolean male;

	public Guard(Location location, boolean male) {
		super(ChatColor.GREEN + "Guard", LEVEL, location, male ? MALE_TEXTURE_DATA : FEMALE_TEXTURE_DATA,
				male ? MALE_TEXTURE_SIGNATURE : FEMALE_TEXTURE_SIGNATURE);
		this.male = male;
	}

	@Override
	protected void onInteract(PlayerCharacter pc) {
		say("Greetings.", pc);
	}

	@Override
	public void say(String dialogue, AbstractCharacter recipient) {
		super.say(dialogue, recipient);
		if (male) {
			MALE_SPEAK_NOISE.play((PlayerCharacter) recipient);
		} else {
			FEMALE_SPEAK_NOISE.play((PlayerCharacter) recipient);
		}
	}

}
