package com.mcmmorpg.impl.npcs;

import org.bukkit.ChatColor;
import org.bukkit.Location;

import com.mcmmorpg.common.character.PlayerCharacter;
import com.mcmmorpg.common.quest.QuestMarker;

public class MasterAlchemist extends StaticHuman {

	private static final int LEVEL = 20;
	private static final String TEXTURE_DATA = "";
	private static final String TEXTURE_SIGNATURE = "";

	public MasterAlchemist(Location location) {
		super(ChatColor.GREEN + "Master Alchemist", LEVEL, location, TEXTURE_DATA, TEXTURE_SIGNATURE);
		QuestMarker.createMarker(location.clone().add(0, 2.25, 0));
	}

	@Override
	protected void onInteract(PlayerCharacter pc) {

	}

}
